*{
    margin: 0;
    padding: 0;
    list-style: none;
}
/* CAROUSEL */
.carousel{
    /* background: #000; */
    height: auto;
    width: 100%;
    cursor: pointer;
    overflow: hidden;
    user-select: none;
    touch-action: none;
}
.carousel *{
    user-select: none;
    touch-action: none;
}

/* CAROUSEL BOX */
.carousel-box{
    width: 30vw;
    margin: 0 auto;
    /* background: #000;  */
    /* height: 10vh; */
}

/* CAROUSEL CONTENT */
.carousel-content{
    display: flex;
    justify-content: space-around;
    position: relative;
    transition: transform 0.25s ease-out;
}

/* CAROUSEL ITEM */
.carousel-item{
    width: 25vw;
    height: 25vw;
    background: #999;
    position: relative;
    opacity: 0.5;
    transform: scale(0.9);
    transition: all 0.25s ease-out;
}
.carousel-item.active{
    transform: scale(1);
    opacity: 1;
}

.carousel-img{
    width: 100%;
    height: auto;
    pointer-events: none;
    position: absolute;
    left: 50%;
    top: 50%;
    transform: translate(-50%, -50%);
}
